$('#template').template(data, {
    //嵌套内容模板，支持传入jQuery对象(单个或数组)/jQuery选择器字符串(单个或数组)
    nested: ['#template_item', '#template_attr', $('#template1'), $('#template2')]
    //是否清除未填充节点
    clean: true,
    //正则查询规则
    rule: /#{(.(?!#{))*[^\n\r\f\t\v\0]}/g
});

