                    }
                ]
            },
            {
                id: 3,
                name: '我是子级'
            }
        ],
        attr: {
            desc: '属性',
            list: [
                {
                    key: 'name',
                    value: '乔治'
                },
                {
                    key: 'sex',
                    value: '男'
                }
            ]
        }
    };
    $('#target').html($('#template').template(data, {nested: '#template_item,#template_attr'}));
});

